const axios = require('axios');
import HeaderHome from "./components/HeaderHome.vue";
export default {
  data() {
    return {
      baseURL: "http://localhost:8000/",
      products: null,
      categories: null
    };
  },
  components: {
    HeaderHome
  },
  methods: {
    async fetchData() {
      await axios.get(this.baseURL + "product/").then(res => this.products = res.data).catch(err => console.log(err));
      await axios.get(this.baseURL + "category/").then(res => this.categories = res.data).catch(err => console.log(err));
    }
  },
  mounted() {
    this.fetchData();
  }
};
